// Devuelve los datos de los n�meros segun el criterio
function getNums($myMode='all'){

	global $siteDB, $magID;

	if($myMode == 'title'){
		$keywords = setSearch($_POST['title'], 'title');
		$queryX = " AND title LIKE '%".$keywords."%' ";
	}

	if($myMode == 'all'){
		$queryX = "";
	}

	$query = "SELECT * 
				FROM cms_nums
				WHERE magID=$magID "
				.$queryX.
				"ORDER BY num DESC";

	$result = $siteDB->query($query);

	while ($row = $siteDB->fetch_array($result, 1)){
		$data[$row['numID']] = $row;
	}

	$siteDB->free_result();
	return $data;

}

function getNum($myID){

	global $siteDB, $magID;

	$query = "SELECT * 
				FROM cms_nums
				WHERE numID=$myID ";

	$result = $siteDB->query($query);

	while ($row = $siteDB->fetch_array($result, 1)){
		$data = $row;
	}

	$siteDB->free_result();
	return $data;

}



