function getCat($myID, $myMode = 'id'){

	global $siteDB;

	if ($myMode == 'id')
		$queryX = "WHERE catID=$myID";
	elseif ($myMode == 'name')
		$queryX = "WHERE name LIKE '%$myID%'";

	$query="SELECT * 
			FROM categories "
			.$queryX. 
			" ORDER BY name DESC";
	$result = $siteDB->query($query);

	$data = $siteDB->query_firstrow($query);

	$siteDB->free_result();
	return $data;

}



