function setSearch($myWord, $myField){

	$myWord = strip_tags($myWord); 
	$myWord = strtr($myWord,".,;:","    ");

	$search0 = explode(" ",$myWord);
	$string = "%' AND ".$myField." LIKE '%";
	$search = implode($string,$search0);

	return $search;
}

