// Convierte la fecha en formato latino dia-mes-a�o hora-min-sec a formato Unix
function convertDateL2U($myDate){
	$aux=explode(" ", $myDate);
	$date=explode("-", $aux[0]);
	$time=explode(":", $aux[1]);
	$myDate=mktime($time[0], $time[1], $time[2], $date[1], $date[0], $date[2]);
	return $myDate;
}

