function getIntervalDates ($myDate = ''){

	// Formato ddmmyyyy
	if(strlen($myDate) == 8){

	}

	// Formato mmyyyy
	elseif(strlen($myDate) == 6){
		$month = substr($myDate, 0, 2);
		$year = substr($myDate, 2, 4);
		$myInterval[0]=mktime(0, 0, 0, $month, 0, $year);
		for($i=31; $i>27; $i--){
			if(checkdate($month, $i, $year)){
				$lastDay = $i;
