function addField($myType, $myName, $myLabel, $myValue='', $mySize='', $myKey=''){

	$this->myType = $myType;
	$this->myName = $myName;
	$this->myLabel = $myLabel;
	$this->myKey = $myKey;
	$this->mySize = $mySize;

	//Si existe alg�n valor, lo incluimos
	if ($_POST[$this->myName] && $this->DataPostOn==1)
		$this->myValue = $_POST[$this->myName];
	else
		$this->myValue = $myValue;


	switch ($this->myType){
		case 'textarea':
			$html = $this->printLabel();
			$html .= $this->printTextarea($myCols, $myRows);
			break;
		case 'hidden':
			$html .= $this->printImput();
			break;
		case 'button':

case 'textarea':
			$html = $this->printLabel();
			$html .= $this->printTextarea($myCols, $myRows);
			break;
		case 'hidden':
			$html .= $this->printImput();
			break;
		case 'button':
			$html = $this->printLabel(1);
			$html .= $this->printImput();
			break;
		case 'submit':
			$html = $this->printLabel(1);
			$html .= $this->printImput();
			break;
		case 'checkbox':
		case 'radio':
			$html = $this->printImput();
			$html .= $this->printLabel();
			break;
		case 'select':
			$html = $this->printLabel();
			$html .= $this->printSelect($multipleOn, $mySelect);
			break;
		default:
			$html = $this->printLabel();
			$html .= $this->printImput(); 
			break; 
		}
	echo $html;
	return;
	}

