function set_error($error) {
	global $form_valido;
	$form_valido = false;
	echo "<p class=\"error\">&raquo; $error</p>\n";
}

/* Valida una cadena de texto, comprueba si est� vac�a y la limpia */
function valida_texto($texto="", $tam = '') {
	if (!$texto) return false;
	if ($tam!='' && strlen($texto) > $tam)
		return false;
	$texto = trim($texto);
	$texto = ucfirst($texto);
	$texto = nl2br($texto);

	return $texto;
}

/* Valida una URL */
function valida_url($url) {	

	global $errorurl;
	// Inicializo la variable de validaci�n
	$valida=true;
	$long=strlen($url);

	// Dos puntos en la URL como en www.dominio.com
	$puntos=0;
	for($i=0;$i<=$long;$i++){
		if(substr($url, $i, 1)=='.'){
			$puntos+=1;
		}	
	}	
	if($puntos<2){
		// Pone las www
		if(ereg("(http://)(.*)",$_POST['url'],$mejorada))
			$_POST['url']=$mejorada[1]."www.".$mejorada[2];
		else 
			$_POST['url']="www.".$_POST['url'];
	}

	// Cadena http:// al comienzo
	if(substr($url, 0, 7)!='http://'){
		$_POST['url']="http://".$_POST['url'];
	}
	// �ltimo caracter no debe ser una slash /
	if(substr($url, -1)=='/')
		$_POST['url']=substr_replace($_POST['url'],"", strlen($_POST['url'])-1,1);

	// Caracteres generales correctos
	if(preg_match('/^(https?|ftp|news)(:\/\/[-_.!~*\'()a-zA-Z0-9;\/?:\@&=+\$,%#]+)$/', $url)==false){
		$valida=false;
		$errorurl.='URL no v�lida<br />';
	}

	return $valida;	
}

