function OpenTag($tag,$attr)
{
    //Etiqueta de apertura
    if($tag=='B' or $tag=='STRONG' or $tag=='H3' or $tag=='H4')
        $this->SetStyle('B',true);
    if($tag=='I' or $tag=='EM')
        $this->SetStyle('I',true);
    if($tag=='U')
        $this->SetStyle('U',true);
    if($tag=='A')
        $this->HREF=$attr['HREF'];
	$this->intag = 1;
    if($tag=='BR'){
        $this->Ln(5);
		$this->intag = 0;
	}
	if($tag=='IMG'){
		$this->IMG = 1;
		$this->putImage($attr);
		$this->intag = 0;		
	}
	if($tag=='LI'){
		$this->SetList(true);
		$this->intag = 0;
		}
}

function CloseTag($tag)
{
    //Etiqueta de cierre
    if($tag=='B' or $tag=='STRONG' or $tag=='H3' or $tag=='H4')
        $this->SetStyle('B',false);
    if($tag=='I' or $tag=='EM'){
        $this->SetStyle('I',false);
		}
    if($tag=='U'){
        $this->SetStyle('U',false);
		}
    if($tag=='A'){
        $this->HREF='';	
		}
	if($tag=='P' || $tag{0}=='H')
		$this->Ln(10);
	if($tag=='LI'){
		$this->Ln(5);
		$this->SetList(false);
		$this->intag = 0;
		}

}

